package rail;

public class Longdouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Addition of two long values//
		long h=7654;
		//long value having more digits means need to give l in input value///
		long j=7865432l;
		long ans=h+j;
		System.out.println("Addition of two long value is : " +ans);
		
		//Addition of two double values//
		double a=4.098766;
		double b=8.654234;
		double c=a+b;
		System.out.println("Addition of two double value is : " +c);
		
		//Addition of two float values//
		float k=12.6f;
		float m=(float)90.7;
		float val=k+m;
		System.out.println("Addition of two float value is : " +val);
		
		//Addition of two char//
		char aa='k';
		String aaa=String.valueOf(aa);
		char bb='k';
		String bbb=String.valueOf(bb);
		String cc=aaa+bbb;
		System.out.println("Addition of two char value is : " +cc);
		
		
		
		
		//All data type()//
		int i=98;
		short r=976;
		byte q=45;
		long y=765432l;
		long result=(long)i+r+q+y;
		System.out.println("Addition of the given input value is : " +result);
		
		
		
		
		

	}

}
