package rail;

public class Mathametics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//String upper case and lower case conversion//
		String str="HELLO WELCOME";
		String str1="this is java program";
		System.out.println(str.toLowerCase());
		System.out.println(str1.toUpperCase());
		
		
		//split method in java
		String ll="hai,hello";
        String[] sp=ll.split(",");
        for(String ss1:sp)
        {
            System.out.println(ss1);
        }
        
        
        //conversion
        
        String st="123456";
        System.out.println(st);
        int num =Integer.parseInt(st);
        
        System.out.println(num);
        
        //mathametical operations
        
        System.out.println(2/1);
        System.out.println(6.0/2);
        
        System.out.println(6/2.0);
      
        System.out.println(6.0/2.0);
       
        System.out.println(0/6);
       
        System.out.println(6/0.0);//Infinity
        System.out.println(6.0/0);//Infinity
        System.out.println(6.0/0.0);//Infinity
                          
       
        System.out.println(0.0/0.0);//NaN
        System.out.println(0.0/9.0);//0.0
        System.out.println(0/0.0);
        System.out.println(0.0/0);
        
        System.out.println('l'/4);
       
       
       
                                                                                                                                                                                                                                                                                                                                                                                                                                                               
       
       
      
		           

	}

}
